	end;
	if sheet.isClasseActive then
		efetivo = efetivo + (tonumber(sheet.magia$(atr)) or 0);
	end;
	if sheet.isMagiaActive then
		efetivo = efetivo + (tonumber(sheet.classe$(atr)) or 0);
	end;			
	sheet.real$(atr) = real;
	sheet.efet$(atr) = efetivo;
end;

