form.script:
local function getMOD(valor)
	if valor~= nil then
		local mod = math.floor(((tonumber(valor) or 10) / 2) - 5);
		if (mod >= 0) then
			mod = "+" .. mod;
		end;					   
		return mod;
	else
		return "+0";
	end;
end;

local function getNumber(text)
	local mod = "0";
	if text~= nil then
		mod = string.gsub(text, "%.", "");
		mod = string.gsub(mod, "k", "000");
		mod = string.gsub(mod, "K", "");
		mod = string.gsub(mod, "g", "");
		mod = string.gsub(mod, "P", "");
		mod = string.gsub(mod, "p", "");
		mod = string.gsub(mod, "O", "");
		mod = string.gsub(mod, "o", "");
		mod = string.gsub(mod, "X", "");
		mod = string.gsub(mod, "x", "");
		mod = string.gsub(mod, " ", "");
		mod = string.gsub(mod, ",", ".");
	end
	return tonumber(mod);
end;

button.event:
local xp = (tonumber(sheet.xpAtual) or 0) + (tonumber(sheet.xpNova) or 0);

sheet.xpAtual = xp;
sheet.xpNova = "0";

dataLink.event:
if sheet~= nil then
	if sheet.xpAtual == nil then
		sheet.xpAtual = 0;
	end;
	local mod = sheet.xpAtual;
	mod = string.gsub(mod, "X", "");
	mod = string.gsub(mod, "P", "");
	mod = string.gsub(mod, "x", "");
	mod = string.gsub(mod, "p", "");
	mod = string.gsub(mod, "%.", "");
	mod = (tonumber(mod) or 0);
	local mod2 = 0;
	local mod3 = 0;
	while mod>=mod2 do
		mod3 = mod3+1;
		mod2 = mod2 + mod3*1000;
	end

	while true do  
		mod2, k = string.gsub(mod2, "^(-?%d+)(%d%d%d)", '%1,%2')
		if (k==0) then
		  break
		end
	end
	mod2 = string.gsub(mod2, ",", ".");

	sheet.xpNivel = mod2 .. "XP";
	sheet.nep = mod3;
end;

